int main()
{
	int hinverted = 0;
	int vinverted = 0;
	int toggle = 0;
	int hmoved = 0;
	int vmoved = 0;
	int Pipe_direction = -1;
	int initial_PipeX_1 = 105;
	int PipeX_1 = 105;
	int PipeX_2 = 70;
	int PipeX_3 = 35;
	int score_counter = 0;
	
	// deteremines the starting postion of the bird 
	uint16_t x = 5;
	uint16_t y = 50;

	uint16_t oldx = x;
	uint16_t oldy = y;
	bool title_screen = true;

	initClock();
	initSysTick();
	setupIO();
	srand(time(NULL));

	while(title_screen)
	{
		// (255,0,0) changes the colour to red 
		printTextX2("Flappy", 30, 30, RGBToWord(255, 0, 0), RGBToWord(0, 0, 0));
		printTextX2("Bird", 30, 50, RGBToWord(255, 0, 0), RGBToWord(0, 0, 0));

		putImage(50,80,20,20,bird1_yel,0,0);

		// (255,255,255) changes the colour to white
		printText("Press up/down", 6, 110, RGBToWord(255, 255, 255), RGBToWord(0, 0, 0));
		printText("to start the game", 6, 120, RGBToWord(255, 255, 255), RGBToWord(0, 0, 0));

		// if the user inputs up or down the game will begin
		if ( (GPIOA->IDR & (1 << 11)) == 0 || (GPIOA->IDR & (1 << 8)) == 0)
		{	
			title_screen = false;
			clear();
			break;
		}
	}

	// main game logic 
	while(1)
	{
		// Initialise variables to track horizontal and vertical movement and inversion
		hmoved = vmoved = 0;
		hinverted = vinverted = 0; 
		
		// displays the bottom floor 
		putImage(0, FlOOR_Y, 128, 43, floor, 0, 0);

		// Check if the Up button is pressed
		// If Up button is pressed (bitwise AND with 1 shifted left by 8)
		if ((GPIOA->IDR & (1 << 8)) == 0)
		{
			// Check if the bird's vertical position is above a certain threshold (16 in this case)
			if (y > 16)
			{
				// Move the bird upward by decrementing its vertical position
				y = y - 2;

				// Set flags to indicate vertical movement and inversion
				vmoved = 1; 
			}
		}

		// If the Up button is not pressed, the bird will fall
		else
		{
			// Check if the bird's vertical position is below a certain threshold (140 in this case)
			if (y < 140)
			{	
				// Move the bird downward by incrementing its vertical position
				y = y + 2;
				
				// Set flags to indicate vertical movement without inversion
				vmoved = 1; 
				vinverted = 0;
			}
		}


		// Check if there has been vertical (vmoved) or horizontal (hmoved) movement
		if ((vmoved) || (hmoved))
		{
			// Only redraw if there has been some movement to reduce flicker
			fillRectangle(oldx, oldy, 17, 13, 0); 

			// Update the old horizontal position
			oldx = x; 

			// Update the old vertical position
			oldy = y;

			// If there has been vertical movement
			if (vmoved)
			{
				// Toggle between two images for a visual effect (flapping wings)
				if (toggle)
					// Display the flappy bird with wings up
					putImage(x, y, 17, 13, flappy_up, hinverted, 0); 

				else	
					// Display the flappy bird with wings down
					putImage(x, y, 17, 13, flappy_down, hinverted, 0);

				// Toggle the value of 'toggle' for the next iteration
				toggle = toggle ^ 1;
			}
		}


		// if the floor is hit by flappy bird then the game ends 
		if (is_floor_hit(x,y) == true )
		{	
			printTextX2("GAME", 30, 30, RGBToWord(255, 255, 0), RGBToWord(0, 0, 0));
			printTextX2("Over", 30, 50, RGBToWord(255, 255, 0), RGBToWord(0, 0, 0));

			delay(2000);
			clear();
			break;

		}
		  
		// Update the x-position of PipeX_1, PipeX_2, and PipeX_3 based on the direction
		PipeX_1 += Pipe_direction;
		PipeX_2 += Pipe_direction;
		PipeX_3 += Pipe_direction;

		// Check if the pipe has moved beyond the screen width
		if (PipeX_1 >= SCREEN_WIDTH || PipeX_1 < 0)
		{
			// Clear the previous position of the top and bottom parts of the pipe
			fillRectangle(PipeX_1 - Pipe_direction, 0, PIPE_WIDTH, PIPE_HEIGHT, 0);
			fillRectangle(PipeX_1 - Pipe_direction, bottomY, PIPE_WIDTH, PIPE_HEIGHT, 0);

			// Increment the score_counter as the player successfully passed the pipe
			score_counter++;

			// If the pipe has moved beyond the screen width, redraw it in its original position
			PipeX_1 = initial_PipeX_1;
		}

		// Check if the pipe has moved beyond the screen width
		if (PipeX_2 >= SCREEN_WIDTH|| PipeX_2 < 0) 
		{	
			// Clear the previous position of the top and bottom parts of the pipe
			fillRectangle(PipeX_2 - Pipe_direction, 0, PIPE_WIDTH,PIPE_HEIGHT, 0);
			fillRectangle(PipeX_2 - Pipe_direction, bottomY, PIPE_WIDTH,PIPE_HEIGHT, 0);

			// Increment the score_counter as the player successfully passed the pipe
			score_counter ++;

			// If the pipe has moved beyond the screen width, redraw it in 
			// the orginal position of pipe 1 
			PipeX_2 = initial_PipeX_1;
		}

		// Check if the pipe has moved beyond the screen width
		if (PipeX_3 >= SCREEN_WIDTH|| PipeX_3 < 0) 
		{	
			// Clear the previous position of the top and bottom parts of the pipe
			fillRectangle(PipeX_3 - Pipe_direction, 0, PIPE_WIDTH,PIPE_HEIGHT, 0);
			fillRectangle(PipeX_3 - Pipe_direction, bottomY, PIPE_WIDTH,PIPE_HEIGHT, 0);

			// Increment the score_counter as the player successfully passed the pipe
			score_counter ++;


			// If the pipe has moved beyond the screen width, redraw it in 
			// the orginal position of pipe 1 
			PipeX_3 = initial_PipeX_1;
				
		}

		//Pipe 1 

		// Clear the previous position of the top part of the pipe
		fillRectangle(PipeX_1 - Pipe_direction, 0,PIPE_WIDTH,PIPE_HEIGHT, 0);

		// Draw the new position of the top part of the pipe
		putImage(PipeX_1, 0, PIPE_WIDTH,PIPE_HEIGHT, top_pipe, 0, 0);

		// Clear the previous position of the bottom part of the pipe
		fillRectangle(PipeX_1 - Pipe_direction, bottomY, PIPE_WIDTH,PIPE_HEIGHT, 0);

		// Draw the new position of the bottom part of the pipe
		putImage(PipeX_1, bottomY, PIPE_WIDTH,PIPE_HEIGHT, bottom_pipe, 0, 0);

		/*
			Repeat the same process for Pipes 2 and 3 
		*/

		//Pipe 2
		fillRectangle(PipeX_2 - Pipe_direction, 0, PIPE_WIDTH,PIPE_HEIGHT, 0);
		putImage(PipeX_2, 0, PIPE_WIDTH,PIPE_HEIGHT, top_pipe, 0, 0);
		fillRectangle(PipeX_2 - Pipe_direction, bottomY, PIPE_WIDTH,PIPE_HEIGHT, 0);
		putImage(PipeX_2, bottomY, PIPE_WIDTH,PIPE_HEIGHT, bottom_pipe, 0, 0);

		// Pipe 3
		fillRectangle(PipeX_3 - Pipe_direction, 0, PIPE_WIDTH,PIPE_HEIGHT, 0);
		putImage(PipeX_3, 0, PIPE_WIDTH,PIPE_HEIGHT, top_pipe, 0, 0);
		fillRectangle(PipeX_3 - Pipe_direction, bottomY, PIPE_WIDTH,PIPE_HEIGHT, 0);
		putImage(PipeX_3, bottomY, PIPE_WIDTH,PIPE_HEIGHT, bottom_pipe, 0, 0);

		// Check if the bird collides with any part of the pipes
		if (isInside(PipeX_1, 4, PIPE_WIDTH,PIPE_HEIGHT, x, y) || 
			isInside(PipeX_1, bottomY - 11, PIPE_WIDTH,PIPE_HEIGHT, x, y) ||
			isInside(PipeX_2, 4, PIPE_WIDTH,PIPE_HEIGHT, x, y) || 
			isInside(PipeX_2, bottomY - 11, PIPE_WIDTH,PIPE_HEIGHT, x, y) ||
			isInside(PipeX_3, 4, PIPE_WIDTH,PIPE_HEIGHT, x, y) || 
			isInside(PipeX_3, bottomY - 11, PIPE_WIDTH,PIPE_HEIGHT, x, y))
        	{
				
				// Clear the entire screen
				clear();

				// if the game is over display the game over screen
				printTextX2("GAME", 30, 30, RGBToWord(255, 255, 0), RGBToWord(0, 0, 0));
				printTextX2("Over", 30, 50, RGBToWord(255, 255, 0), RGBToWord(0, 0, 0));

				printTextX2("Score : ", 30, 90, RGBToWord(255, 255, 0), RGBToWord(0, 0, 0));
				printNumberX2(score_counter,30, 110, RGBToWord(255, 255, 0), RGBToWord(0, 0, 0));

				delay(100000);
				clear();
				break;
			}

			// adjust the delay to change the speed of the game
			delay(10);

	}
			
	return 0;
}

	


void initSysTick(void)
{
	SysTick->LOAD = 48000;
	SysTick->CTRL = 7;
	SysTick->VAL = 10;
	__asm(" cpsie i "); // enable interrupts
}
void SysTick_Handler(void)
{
	milliseconds++;
}
void initClock(void)
{
// This is potentially a dangerous function as it could
// result in a system with an invalid clock signal - result: a stuck system
        // Set the PLL up
        // First ensure PLL is disabled
        RCC->CR &= ~(1u<<24);
        while( (RCC->CR & (1 <<25))); // wait for PLL ready to be cleared
        
// Warning here: if system clock is greater than 24MHz then wait-state(s) need to be
// inserted into Flash memory interface
				
        FLASH->ACR |= (1 << 0);
        FLASH->ACR &=~((1u << 2) | (1u<<1));
        // Turn on FLASH prefetch buffer
        FLASH->ACR |= (1 << 4);
        // set PLL multiplier to 12 (yielding 48MHz)
        RCC->CFGR &= ~((1u<<21) | (1u<<20) | (1u<<19) | (1u<<18));
        RCC->CFGR |= ((1<<21) | (1<<19) ); 

        // Need to limit ADC clock to below 14MHz so will change ADC prescaler to 4
        RCC->CFGR |= (1<<14);

        // and turn the PLL back on again
        RCC->CR |= (1<<24);        
        // set PLL as system clock source 
        RCC->CFGR |= (1<<1);
}
void delay(volatile uint32_t dly)
{
	uint32_t end_time = dly + milliseconds;
	while(milliseconds != end_time)
		__asm(" wfi "); // sleep
}
void enablePullUp(GPIO_TypeDef *Port, uint32_t BitNumber)
{
	Port->PUPDR = Port->PUPDR &~(3u << BitNumber*2); // clear pull-up resistor bits
	Port->PUPDR = Port->PUPDR | (1u << BitNumber*2); // set pull-up bit
}
void pinMode(GPIO_TypeDef *Port, uint32_t BitNumber, uint32_t Mode)
{
	/*
	*/
	uint32_t mode_value = Port->MODER;
	Mode = Mode << (2 * BitNumber);
	mode_value = mode_value & ~(3u << (BitNumber * 2));
	mode_value = mode_value | Mode;
	Port->MODER = mode_value;
}
int isInside(uint16_t x1, uint16_t y1, uint16_t w, uint16_t h, uint16_t px, uint16_t py)
{
	// checks to see if point px,py is within the rectange defined by x,y,w,h
	uint16_t x2,y2;
	x2 = x1+w;
	y2 = y1+h;
	int rvalue = 0;
	if ( (px >= x1) && (px <= x2))
	{
		// ok, x constraint met
		if ( (py >= y1) && (py <= y2))
			rvalue = 1;
	}
	return rvalue;
}
void setupIO()
{
	RCC->AHBENR |= (1 << 18) + (1 << 17); // enable Ports A and B
	display_begin();
	pinMode(GPIOB,4,0);
	pinMode(GPIOB,5,0);
	pinMode(GPIOA,8,0);
	pinMode(GPIOA,11,0);
	enablePullUp(GPIOB,4);
	enablePullUp(GPIOB,5);
	enablePullUp(GPIOA,11);
	enablePullUp(GPIOA,8);
}

bool is_floor_hit(int x,int y)
{
	return(y >=SCREEN_HEIGHT - 20);
}

